mapper.update:
update t_comment
set bad=bad + 1
where comment_id = #{id}

update t_comment
set good=good + 1
where comment_id = #{id}

update t_blog
set  like = like  + 1
where blog_id = #{id}

update t_blog
set click=click + 1
where blog_id = #{id}

update t_link
set click=click + 1
where link_id = #{id}

mapper.select:
select b.id, b.category_id, b.title, b.summary, b.weight, b.header_img, b.status, b.support, b.like, b.click, b.comment, b.create_time, b.update_time, count(c.id) as comment_count from t_blog b left join t_comment c on b.blog_id=c.page_id
AND b.category_id =#{categoryId}
AND b.support = #{support}
and date_format( b.create_date,'%y%m%d') >= date_format(#{beginDate},'%y%m%d')
and date_format( b.create_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d')
group by b.blog_id

        select t.tag_id, color, title,type, create_date,
update_date
        from t_tag t
        left join t_blog_tag_mid tm on t.tag_id = tm.tag_id
        where t.type=#{type}
        and tm.blog_id=#{id}
        and tm.book_id=#{id}
