mapper.update:
update t_comment
set bad=bad + 1
where id = #{id}

update t_comment
set good=good + 1
where id = #{id}

update t_blog
set  like = like  + 1
where id = #{id}

update t_blog
set click=click + 1
where id = #{id}

update t_link
set click=click + 1
where id = #{id}

mapper.select:
select b.id, b.category_id, b.title, b.summary, b.weight, b.header_img, b.status, b.support, b.like, b.click, b.comment, b.create_date, b.update_date, count(c.id) as comment_count from t_blog b left join t_comment c on b.id=c.page_id
AND b.category_id =#{categoryId}
AND b.support = #{support}
and date_format( b.create_date,'%y%m%d') >= date_format(#{beginDate},'%y%m%d')
and date_format( b.create_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d')
group by b.id
